import React, { useState } from 'react';
import { useVessel } from '../context/VesselContext';
import { maintenanceSchedule } from '../config/maintenanceSchedule';
import { Ship, Anchor, Calendar, Flag, Wrench } from 'lucide-react';

const Operations = () => {
  const {
    records,
    addRecord,
    equipmentHours,
    updateEquipmentHours,
    maintenanceAlerts,
    addMaintenanceAlert,
    deleteRecord,
  } = useVessel();

  // État pour le formulaire d'opération
  const [formData, setFormData] = useState({
    shipName: '',
    shipType: '',
    status: 'navigation',
    activity: '',
    equipmentName: '',
    workingStartTime: '',
    workingStopTime: '',
    remarks: '',
  });

  // Configuration des types de navires
  const shipTypeConfigurations = {
    tanker: {
      status: ['loading', 'discharging', 'ballasting', 'tank cleaning', 'inerting', 'COW'],
      equipment: [
        'Cargo Pumps',
        'Inert Gas System',
        'COW System',
        'Heating System',
        'Main Engine',
        'Auxiliary Engine',
        'Generator',
      ],
      activities: [
        'Cargo Operation',
        'Tank Cleaning',
        'Inerting',
        'Bunkering',
        'Maintenance',
        'Safety Rounds',
      ],
    },
    container: {
      status: ['loading', 'discharging', 'lashing', 'unlashing'],
      equipment: [
        'Container Crane',
        'Reefer System',
        'Lashing Equipment',
        'Main Engine',
        'Auxiliary Engine',
        'Generator',
      ],
      activities: [
        'Container Loading',
        'Container Unloading',
        'Reefer Monitoring',
        'Lashing Operation',
        'Maintenance',
      ],
    },
    bulkCarrier: {
      status: ['loading', 'discharging', 'hold cleaning', 'ventilating'],
      equipment: [
        'Cargo Crane',
        'Ventilation System',
        'Conveyor System',
        'Main Engine',
        'Auxiliary Engine',
        'Generator',
      ],
      activities: [
        'Bulk Loading',
        'Bulk Discharging',
        'Hold Cleaning',
        'Ventilation',
        'Maintenance',
      ],
    },
  };

  // Calculer les heures de travail
  const calculateWorkingHours = (startTime, stopTime) => {
    const start = new Date(startTime);
    const stop = new Date(stopTime);
    return (stop - start) / (1000 * 60 * 60);
  };

  // Vérifier la maintenance nécessaire
  const checkMaintenance = (equipmentName, totalHours) => {
    const schedule = maintenanceSchedule[equipmentName];
    if (!schedule) return [];

    const alerts = [];
    if (totalHours >= schedule.majorServiceHours) {
      alerts.push({
        type: 'major',
        message: `Major maintenance required for ${equipmentName} (${totalHours.toFixed(1)} hours)`,
        priority: 'high',
      });
    } else if (totalHours >= schedule.minorServiceHours) {
      alerts.push({
        type: 'minor',
        message: `Minor maintenance required for ${equipmentName} (${totalHours.toFixed(1)} hours)`,
        priority: 'medium',
      });
    } else if (totalHours >= schedule.inspectionHours) {
      alerts.push({
        type: 'inspection',
        message: `Inspection required for ${equipmentName} (${totalHours.toFixed(1)} hours)`,
        priority: 'low',
      });
    }
    return alerts;
  };

  // Gérer la soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();

    if (new Date(formData.workingStopTime) <= new Date(formData.workingStartTime)) {
      alert('Stop time must be after start time');
      return;
    }

    const workingHours = calculateWorkingHours(
      formData.workingStartTime,
      formData.workingStopTime
    );

    updateEquipmentHours(formData.equipmentName, workingHours);

    const newRecord = {
      id: Date.now(),
      ...formData,
      workingHours,
      dateAdded: new Date().toLocaleString(),
    };

    addRecord(newRecord);

    // Vérifier la maintenance
    const totalHours = equipmentHours[formData.equipmentName] + workingHours;
    const maintenanceNeeded = checkMaintenance(formData.equipmentName, totalHours);

    if (maintenanceNeeded.length > 0) {
      maintenanceNeeded.forEach((alert) => addMaintenanceAlert(alert));
    }

    // Réinitialiser le formulaire
    setFormData({
      shipName: '',
      shipType: '',
      status: 'navigation',
      activity: '',
      equipmentName: '',
      workingStartTime: '',
      workingStopTime: '',
      remarks: '',
    });
  };

  // Gérer les changements du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="space-y-6">
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
          <Wrench className="h-5 w-5 mr-2 text-blue-500" />
          New Operation Record
        </h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">Ship Name</label>
              <input
                type="text"
                name="shipName"
                value={formData.shipName}
                onChange={handleChange}
                required
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Ship Type</label>
              <select
                name="shipType"
                value={formData.shipType}
                onChange={handleChange}
                required
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              >
                <option value="">Select ship type</option>
                {Object.keys(shipTypeConfigurations).map((type) => (
                  <option key={type} value={type}>
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </option>
                ))}
              </select>
            </div>
            {formData.shipType && (
              <>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Status</label>
                  <select
                    name="status"
                    value={formData.status}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  >
                    <option value="">Select status</option>
                    {shipTypeConfigurations[formData.shipType].status.map((stat) => (
                      <option key={stat} value={stat}>
                        {stat.charAt(0).toUpperCase() + stat.slice(1)}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Activity</label>
                  <select
                    name="activity"
                    value={formData.activity}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  >
                    <option value="">Select activity</option>
                    {shipTypeConfigurations[formData.shipType].activities.map((activity) => (
                      <option key={activity} value={activity}>
                        {activity}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Equipment</label>
                  <select
                    name="equipmentName"
                    value={formData.equipmentName}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  >
                    <option value="">Select equipment</option>
                    {shipTypeConfigurations[formData.shipType].equipment.map((eq) => (
                      <option key={eq} value={eq}>
                        {eq}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Start Time</label>
                  <input
                    type="datetime-local"
                    name="workingStartTime"
                    value={formData.workingStartTime}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Stop Time</label>
                  <input
                    type="datetime-local"
                    name="workingStopTime"
                    value={formData.workingStopTime}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  />
                </div>
              </>
            )}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Remarks</label>
            <textarea
              name="remarks"
              value={formData.remarks}
              onChange={handleChange}
              rows="3"
              className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
            />
          </div>
          <div className="flex justify-end space-x-4">
            <button
              type="button"
              onClick={() =>
                setFormData({
                  shipName: '',
                  shipType: '',
                  status: 'navigation',
                  activity: '',
                  equipmentName: '',
                  workingStartTime: '',
                  workingStopTime: '',
                  remarks: '',
                })
              }
              className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
            >
              Clear
            </button>
            <button
              type="submit"
              className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Submit
            </button>
          </div>
        </form>
      </div>

      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
          <Ship className="h-5 w-5 mr-2 text-blue-500" />
          Operation Records
        </h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Ship
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Type
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Activity
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Equipment
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Hours
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {records.map((record) => (
                <tr key={record.id}>
                  <td className="px-6 py-4 whitespace-nowrap">{record.shipName}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{record.shipType}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{record.status}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{record.activity}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{record.equipmentName}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{record.workingHours.toFixed(1)}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <button
                      onClick={() => deleteRecord(record.id)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Operations;
