// src/context/VesselContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';

const VesselContext = createContext();

export const VesselProvider = ({ children }) => {
  const [vessels, setVessels] = useState([]);

  useEffect(() => {
    const storedVessels = JSON.parse(localStorage.getItem("vessels") || "[]");
    setVessels(storedVessels);
  }, []);

  useEffect(() => {
    if (vessels.length > 0) {
      localStorage.setItem("vessels", JSON.stringify(vessels));
    }
  }, [vessels]);

  const addVessel = (newVessel) => {
    setVessels(prevVessels => [...prevVessels, newVessel]);
  };

  return (
    <VesselContext.Provider value={{ vessels, addVessel }}>
      {children}
    </VesselContext.Provider>
  );
};

export const useVessel = () => useContext(VesselContext);
