import React, { useState } from 'react';
import { UserPlus, Mail, Lock, Building, Ship, Phone, MapPin } from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import { useUsers } from '../../context/UserContext';

function Register({ onSwitchToLogin }) {
  const { login } = useAuth();
  const { addUser } = useUsers();
  const [error, setError] = useState('');

  const [formData, setFormData] = useState({
    // Informations de l'entreprise
    companyName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phoneNumber: '',
    address: '',
    // Informations de la flotte
    vessels: [
      {
        vesselName: '',
        vesselType: '',
        imoNumber: '',
        yearBuilt: ''
      }
    ]
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    // Validations
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    try {
      // CrÃ©er le compte entreprise
      const newCompany = {
        id: Date.now(),
        companyName: formData.companyName,
        email: formData.email,
        phoneNumber: formData.phoneNumber,
        address: formData.address,
        role: 'company',
        status: 'active',
        dateCreated: new Date().toISOString(),
        vessels: formData.vessels.filter(v => v.vesselName && v.vesselType),
        permissions: ['view_own', 'edit_own']
      };

      addUser(newCompany);

      // Connecter l'entreprise
      login({
        user: formData.companyName,
        role: 'company',
        companyId: newCompany.id,
        token: `company-${Date.now()}`
      });
    } catch (err) {
      setError('Failed to create account');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleVesselChange = (index, field, value) => {
    setFormData(prev => {
      const newVessels = [...prev.vessels];
      newVessels[index] = {
        ...newVessels[index],
        [field]: value
      };
      return {
        ...prev,
        vessels: newVessels
      };
    });
  };

  const addVessel = () => {
    setFormData(prev => ({
      ...prev,
      vessels: [...prev.vessels, { vesselName: '', vesselType: '', imoNumber: '', yearBuilt: '' }]
    }));
  };

  const removeVessel = (index) => {
    setFormData(prev => ({
      ...prev,
      vessels: prev.vessels.filter((_, i) => i !== index)
    }));
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl w-full space-y-8">
        <div>
          <Building className="mx-auto h-12 w-12 text-blue-600" />
          <h2 className="mt-6 text-center text-3xl font-bold text-gray-900">
            Register Your Company
          </h2>
        </div>

        <form onSubmit={handleSubmit} className="mt-8 space-y-6">
          {error && (
            <div className="bg-red-50 border-l-4 border-red-400 p-4">
              <p className="text-sm text-red-700">{error}</p>
            </div>
          )}

          <div className="bg-white shadow-md rounded-lg p-6 space-y-6">
            <h3 className="text-lg font-medium text-gray-900">Company Information</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="companyName" className="block text-sm font-medium text-gray-700">
                  Company Name
                </label>
                <div className="mt-1 relative">
                  <Building className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                  <input
                    id="companyName"
                    name="companyName"
                    type="text"
                    required
                    className="pl-10 w-full rounded-md border border-gray-300 p-2"
                    value={formData.companyName}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                  Email
                </label>
                <div className="mt-1 relative">
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                  <input
                    id="email"
                    name="email"
                    type="email"
                    required
                    className="pl-10 w-full rounded-md border border-gray-300 p-2"
                    value={formData.email}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div>
                <label htmlFor="phoneNumber" className="block text-sm font-medium text-gray-700">
                  Phone Number
                </label>
                <div className="mt-1 relative">
                  <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                  <input
                    id="phoneNumber"
                    name="phoneNumber"
                    type="tel"
                    required
                    className="pl-10 w-full rounded-md border border-gray-300 p-2"
                    value={formData.phoneNumber}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div>
                <label htmlFor="address" className="block text-sm font-medium text-gray-700">
                  Address
                </label>
                <div className="mt-1 relative">
                  <MapPin className="absolute left-3 top-3 h-5 w-5 text-gray-400" />
                  <textarea
                    id="address"
                    name="address"
                    required
                    className="pl-10 w-full rounded-md border border-gray-300 p-2"
                    value={formData.address}
                    onChange={handleChange}
                    rows={2}
                  />
                </div>
              </div>

              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                  Password
                </label>
                <div className="mt-1 relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                  <input
                    id="password"
                    name="password"
                    type="password"
                    required
                    className="pl-10 w-full rounded-md border border-gray-300 p-2"
                    value={formData.password}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div>
                <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
                  Confirm Password
                </label>
                <div className="mt-1 relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                  <input
                    id="confirmPassword"
                    name="confirmPassword"
                    type="password"
                    required
                    className="pl-10 w-full rounded-md border border-gray-300 p-2"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                  />
                </div>
              </div>
            </div>
          </div>

          <div className="bg-white shadow-md rounded-lg p-6 space-y-6">
            <div className="flex justify-between items-center">
              <h3 className="text-lg font-medium text-gray-900">Vessels</h3>
              <button
                type="button"
                onClick={addVessel}
                className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700"
              >
                <Ship className="h-4 w-4 mr-2" />
                Add Vessel
              </button>
            </div>

            {formData.vessels.map((vessel, index) => (
              <div key={index} className="border rounded-lg p-4 space-y-4">
                <div className="flex justify-between items-center">
                  <h4 className="text-md font-medium text-gray-900">Vessel {index + 1}</h4>
                  {index > 0 && (
                    <button
                      type="button"
                      onClick={() => removeVessel(index)}
                      className="text-red-600 hover:text-red-800"
                    >
                      Remove
                    </button>
                  )}
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">
                      Vessel Name
                    </label>
                    <input
                      type="text"
                      required
                      className="mt-1 w-full rounded-md border border-gray-300 p-2"
                      value={vessel.vesselName}
                      onChange={(e) => handleVesselChange(index, 'vesselName', e.target.value)}
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">
                      Vessel Type
                    </label>
                    <select
                      required
                      className="mt-1 w-full rounded-md border border-gray-300 p-2"
                      value={vessel.vesselType}
                      onChange={(e) => handleVesselChange(index, 'vesselType', e.target.value)}
                    >
                      <option value="">Select type</option>
                      <option value="tanker">Tanker</option>
                      <option value="container">Container Ship</option>
                      <option value="bulkCarrier">Bulk Carrier</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">
                      IMO Number
                    </label>
                    <input
                      type="text"
                      required
                      className="mt-1 w-full rounded-md border border-gray-300 p-2"
                      value={vessel.imoNumber}
                      onChange={(e) => handleVesselChange(index, 'imoNumber', e.target.value)}
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">
                      Year Built
                    </label>
                    <input
                      type="number"
                      required
                      min="1900"
                      max={new Date().getFullYear()}
                      className="mt-1 w-full rounded-md border border-gray-300 p-2"
                      value={vessel.yearBuilt}
                      onChange={(e) => handleVesselChange(index, 'yearBuilt', e.target.value)}
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="flex justify-between items-center">
            <button
              type="button"
              onClick={onSwitchToLogin}
              className="text-blue-600 hover:text-blue-500"
            >
              Already have an account? Sign in
            </button>

            <button
              type="submit"
              className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
            >
              <UserPlus className="h-5 w-5 mr-2" />
              Register Company
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Register;
