import React, { useState } from "react";
import { Ship, Plus, FileText, Wrench } from "lucide-react"; // Importez les icônes
import { useVessel } from "../../context/VesselContext"; // Importez useVessel

function AddVessel() {
  const { addVessel } = useVessel();
  const [vesselData, setVesselData] = useState({
    // Informations de base
    vesselName: "",
    imoNumber: "",
    vesselType: "",
    yearBuilt: "",
    flag: "",

    // Caractéristiques techniques
    length: "",
    beam: "",
    draft: "",
    grossTonnage: "",
    deadWeight: "",

    // Équipements principaux
    mainEngine: {
      brand: "",
      model: "",
      power: "",
      rpm: "",
    },
    auxiliaryEngines: [],
    generators: [],

    // Certifications
    certificates: [],
  });

  const vesselTypes = [
    "Tanker",
    "Container Ship",
    "Bulk Carrier",
    "General Cargo",
    "Car Carrier",
    "LNG Carrier",
    "Passenger Ship",
    "Fishing Vessel",
    "Offshore Support",
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setVesselData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleMainEngineChange = (e) => {
    const { name, value } = e.target;
    setVesselData((prev) => ({
      ...prev,
      mainEngine: {
        ...prev.mainEngine,
        [name]: value,
      },
    }));
  };

  // Fonctions pour gérer les tableaux dynamiques (auxiliaryEngines, generators, certificates)
  const handleAddAuxiliaryEngine = () => {
    setVesselData((prev) => ({
      ...prev,
      auxiliaryEngines: [
        ...prev.auxiliaryEngines,
        { brand: "", model: "", power: "", rpm: "" },
      ],
    }));
  };

  const handleRemoveAuxiliaryEngine = (index) => {
    setVesselData((prev) => ({
      ...prev,
      auxiliaryEngines: prev.auxiliaryEngines.filter((_, i) => i !== index),
    }));
  };

  const handleAuxiliaryEngineChange = (index, e) => {
    const { name, value } = e.target;
    setVesselData((prev) => ({
      ...prev,
      auxiliaryEngines: prev.auxiliaryEngines.map((engine, i) =>
        i === index ? { ...engine, [name]: value } : engine
      ),
    }));
  };

  // Fonctions pour gérer les tableaux dynamiques des générateurs
  const handleAddGenerator = () => {
    setVesselData((prev) => ({
      ...prev,
      generators: [
        ...prev.generators,
        { brand: "", model: "", power: "", type: "" },
      ],
    }));
  };

  const handleRemoveGenerator = (index) => {
    setVesselData((prev) => ({
      ...prev,
      generators: prev.generators.filter((_, i) => i !== index),
    }));
  };

  const handleGeneratorChange = (index, e) => {
    const { name, value } = e.target;
    setVesselData((prev) => ({
      ...prev,
      generators: prev.generators.map((generator, i) =>
        i === index ? { ...generator, [name]: value } : generator
      ),
    }));
  };

  // Fonctions pour gérer les tableaux dynamiques des certifications
  const handleAddCertificate = () => {
    setVesselData((prev) => ({
      ...prev,
      certificates: [
        ...prev.certificates,
        { name: "", issueDate: "", expiryDate: "", issuingAuthority: "" },
      ],
    }));
  };

  const handleRemoveCertificate = (index) => {
    setVesselData((prev) => ({
      ...prev,
      certificates: prev.certificates.filter((_, i) => i !== index),
    }));
  };

  const handleCertificateChange = (index, e) => {
    const { name, value } = e.target;
    setVesselData((prev) => ({
      ...prev,
      certificates: prev.certificates.map((certificate, i) =>
        i === index ? { ...certificate, [name]: value } : certificate
      ),
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("handleSubmit appelé"); // Pour le débogage
    console.log("addVessel:", addVessel); // Pour le débogage
    addVessel(vesselData); // On appelle addVessel du contexte avec les données du formulaire
    // Réinitialiser le formulaire ici
    setVesselData({
      vesselName: "",
      imoNumber: "",
      vesselType: "",
      yearBuilt: "",
      flag: "",
      length: "",
      beam: "",
      draft: "",
      grossTonnage: "",
      deadWeight: "",
      mainEngine: {
        brand: "",
        model: "",
        power: "",
        rpm: "",
      },
      auxiliaryEngines: [],
      generators: [],
      certificates: [],
    });
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <form onSubmit={handleSubmit} className="space-y-8">
        {/* Informations de base */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <Ship className="h-5 w-5 mr-2 text-blue-500" />
            Basic Information
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Vessel Name
              </label>
              <input
                type="text"
                name="vesselName"
                value={vesselData.vesselName}
                onChange={handleChange}
                required
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                IMO Number
              </label>
              <input
                type="text"
                name="imoNumber"
                value={vesselData.imoNumber}
                onChange={handleChange}
                required
                pattern="[0-9]{7}"
                title="IMO number must be 7 digits"
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Vessel Type
              </label>
              <select
                name="vesselType"
                value={vesselData.vesselType}
                onChange={handleChange}
                required
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              >
                <option value="">Select type</option>
                {vesselTypes.map((type) => (
                  <option key={type} value={type}>
                    {type}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Year Built
              </label>
              <input
                type="number"
                name="yearBuilt"
                value={vesselData.yearBuilt}
                onChange={handleChange}
                min="1900"
                max={new Date().getFullYear()}
                required
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Flag
              </label>
              <input
                type="text"
                name="flag"
                value={vesselData.flag}
                onChange={handleChange}
                required
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
          </div>
        </div>
        {/* Caractéristiques techniques */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <FileText className="h-5 w-5 mr-2 text-blue-500" />
            Technical Specifications
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Length (in meters)
              </label>
              <input
                type="number"
                name="length"
                value={vesselData.length}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Beam (in meters)
              </label>
              <input
                type="number"
                name="beam"
                value={vesselData.beam}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Draft (in meters)
              </label>
              <input
                type="number"
                name="draft"
                value={vesselData.draft}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Gross Tonnage
              </label>
              <input
                type="number"
                name="grossTonnage"
                value={vesselData.grossTonnage}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Deadweight
              </label>
              <input
                type="number"
                name="deadWeight"
                value={vesselData.deadWeight}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
              />
            </div>
          </div>
        </div>
        {/* Équipements principaux */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <Wrench className="h-5 w-5 mr-2 text-blue-500" />
            Main Equipments
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Main Engine Details */}
            <div>
              <h3 className="text-md font-medium text-gray-700 mb-2">
                Main Engine
              </h3>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Brand
                  </label>
                  <input
                    type="text"
                    name="brand"
                    value={vesselData.mainEngine.brand}
                    onChange={handleMainEngineChange}
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Model
                  </label>
                  <input
                    type="text"
                    name="model"
                    value={vesselData.mainEngine.model}
                    onChange={handleMainEngineChange}
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  />
                </div>
                <div>

                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Power (kW)
                  </label>
                  <input
                    type="number"
                    name="power"
                    value={engine.power}
                    onChange={(e) => handleAuxiliaryEngineChange(index, e)}
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    RPM
                  </label>
                  <input
                    type="number"
                    name="rpm"
                    value={engine.rpm}
                    onChange={(e) => handleAuxiliaryEngineChange(index, e)}
                    className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                  />
                </div>
                <div className="col-span-2 flex justify-end">
                  <button
                    type="button"
                    onClick={() => handleRemoveAuxiliaryEngine(index)}
                    className="text-red-500 hover:text-red-700"
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))
          </div>
        </div>
        </div>

        {/* Generators */}
        <div>
          <div className="flex items-center justify-between mb-2">
            <h3 className="text-md font-medium text-gray-700">Generators</h3>
            <button
              type="button"
              onClick={handleAddGenerator}
              className="text-blue-500 hover:text-blue-700"
            >
              <Plus className="h-4 w-4" />
            </button>
          </div>
          {vesselData.generators.map((generator, index) => (
            <div
              key={index}
              className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4 border rounded-md p-4"
            >
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Brand
                </label>
                <input
                  type="text"
                  name="brand"
                  value={engine.brand}
                  onChange={(e) => handleGeneratorChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Model
                </label>
                <input
                  type="text"
                  name="model"
                  value={generator.model}
                  onChange={(e) => handleGeneratorChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Power (kW)
                </label>
                <input
                  type="number"
                  name="power"
                  value={generator.power}
                  onChange={(e) => handleGeneratorChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Type
                </label>
                <input
                  type="text"
                  name="type"
                  value={generator.type}
                  onChange={(e) => handleGeneratorChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div className="col-span-2 flex justify-end">
                <button
                  type="button"
                  onClick={() => handleRemoveGenerator(index)}
                  className="text-red-500 hover:text-red-700"
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Certifications */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <FileText className="h-5 w-5 mr-2 text-blue-500" />
            Certifications
          </h2>
          <div className="flex items-center justify-between mb-2">
            <h3 className="text-md font-medium text-gray-700">
              List of Certificates
            </h3>
            <button
              type="button"
              onClick={handleAddCertificate}
              className="text-blue-500 hover:text-blue-700"
            >
              <Plus className="h-4 w-4" />
            </button>
          </div>
          {vesselData.certificates.map((certificate, index) => (
            <div
              key={index}
              className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4 border rounded-md p-4"
            >
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={certificate.name}
                  onChange={(e) => handleCertificateChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Issue Date
                </label>
                <input
                  type="date"
                  name="issueDate"
                  value={certificate.issueDate}
                  onChange={(e) => handleCertificateChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Expiry Date
                </label>
                <input
                  type="date"
                  name="expiryDate"
                  value={certificate.expiryDate}
                  onChange={(e) => handleCertificateChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Issuing Authority
                </label>
                <input
                  type="text"
                  name="issuingAuthority"
                  value={certificate.issuingAuthority}
                  onChange={(e) => handleCertificateChange(index, e)}
                  className="mt-1 block w-full rounded-md border border-gray-300 shadow-sm p-2 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
                />
              </div>
              <div className="col-span-2 flex justify-end">
                <button
                  type="button"
                  onClick={() => handleRemoveCertificate(index)}
                  className="text-red-500 hover:text-red-700"
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Boutons de soumission */}
        <div className="flex justify-end space-x-4">
          <button
            type="button"
            onClick={() => {
              // Réinitialiser le formulaire
              setVesselData({
                vesselName: "",
                imoNumber: "",
                vesselType: "",
                yearBuilt: "",
                flag: "",
                length: "",
                beam: "",
                draft: "",
                grossTonnage: "",
                deadWeight: "",
                mainEngine: {
                  brand: "",
                  model: "",
                  power: "",
                  rpm: "",
                },
                auxiliaryEngines: [],
                generators: [],
                certificates: [],
              });
            }}
            className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            <Plus className="h-5 w-5 mr-2 inline" />
            Add Vessel
          </button>
        </div>
      </form>
    </div>
  );
}

export default AddVessel;
