import React from 'react';
import { useVessel } from '../context/VesselContext'; // Importez le hook personnalisé
import { Ship, Anchor, Calendar, Flag } from 'lucide-react';

const Operations = () => {
  // Utilisez la liste des navires du contexte
  const { vessels } = useVessel();

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h2 className="text-2xl font-bold text-gray-900 mb-6">Opérations</h2>

      {vessels.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {vessels.map((vessel) => (
            <div
              key={vessel.imoNumber}
              className="bg-white shadow rounded-lg p-6"
            >
              <div className="flex items-center space-x-4 mb-4">
                <Ship className="h-8 w-8 text-blue-500" />
                <h3 className="text-xl font-semibold text-gray-900">
                  {vessel.vesselName}
                </h3>
              </div>
              <div className="space-y-2">
                <div className="flex items-center">
                  <Flag className="h-5 w-5 text-gray-500 mr-2" />
                  <p className="text-gray-700">{vessel.flag}</p>
                </div>
                <div className="flex items-center">
                  <Calendar className="h-5 w-5 text-gray-500 mr-2" />
                  <p className="text-gray-700">{vessel.yearBuilt}</p>
                </div>
                <div className="flex items-center">
                  <Anchor className="h-5 w-5 text-gray-500 mr-2" />
                  <p className="text-gray-700">{vessel.vesselType}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-gray-500">Aucun navire ajouté pour le moment.</p>
      )}
    </div>
  );
};

export default Operations;
