import React, { useState, useEffect } from 'react';
import Dashboard from './components/Dashboard';
import FinancialModule from './components/FinancialModule';
import { 
  Ship, 
  Navigation, 
  Anchor, 
  RotateCw,
  Wrench,
  AlertTriangle,
  ChartBar,
  DollarSign,
  Activity,
  Clock
} from 'lucide-react';

// Configuration de maintenance
const maintenanceSchedule = {
  'Main Engine': {
    inspectionHours: 2000,
    minorServiceHours: 5000,
    majorServiceHours: 10000
  },
  'Auxiliary Engine': {
    inspectionHours: 1500,
    minorServiceHours: 4000,
    majorServiceHours: 8000
  },
  'Generator': {
    inspectionHours: 1200,
    minorServiceHours: 3500,
    majorServiceHours: 7000
  },
  'Boiler': {
    inspectionHours: 1000,
    minorServiceHours: 3000,
    majorServiceHours: 6000
  },
  'Pump': {
    inspectionHours: 1000,
    minorServiceHours: 3000,
    majorServiceHours: 6000
  },
  'Compressor': {
    inspectionHours: 800,
    minorServiceHours: 2500,
    majorServiceHours: 5000
  },
  'Crane': {
    inspectionHours: 500,
    minorServiceHours: 2000,
    majorServiceHours: 4000
  },
  'Propeller': {
    inspectionHours: 2500,
    minorServiceHours: 5000,
    majorServiceHours: 10000
  }
};

// Composants de base
const Card = ({ children }) => <div className="bg-white shadow rounded-lg overflow-hidden">{children}</div>;
const CardHeader = ({ children }) => <div className="px-6 py-4 border-b border-gray-200">{children}</div>;
const CardTitle = ({ children }) => <h2 className="text-lg font-medium text-gray-900">{children}</h2>;
const CardContent = ({ children }) => <div className="p-6">{children}</div>;

function App() {
  // État actif de l'onglet
  const [activeTab, setActiveTab] = useState('dashboard');

  // États du formulaire
  const [formData, setFormData] = useState({
    shipName: "",
    shipType: "",
    status: "navigation",
    activity: "",
    equipmentName: "",
    workingStartTime: "",
    workingStopTime: "",
    remarks: ""
  });

  // États pour la gestion des données
  const [records, setRecords] = useState([]);
  const [equipmentHours, setEquipmentHours] = useState({});
  const [maintenanceAlerts, setMaintenanceAlerts] = useState([]);

  // Configuration des statuts des navires
  const vesselStatus = {
    navigation: { icon: <Navigation className="icon" /> },
    anchorage: { icon: <Anchor className="icon" /> },
    drifting: { icon: <Ship className="icon" /> },
    inPort: { icon: <Ship className="icon" /> },
    maneuvering: { icon: <RotateCw className="icon" /> }
  };

  // Liste des activités
  const activities = [
    "Idle", "Loading", "Discharging", "Bunkering", "Supply",
    "Maintenance", "Emergency Repair", "Routine Check", "Training",
    "Inspection", "Cleaning", "Testing", "Calibration", "Overhaul"
  ];

  // Liste des types de navires
  const shipTypes = [
    "Cargo", "Tanker", "Container Ship", "Bulk Carrier",
    "Passenger Ship", "Fishing Vessel", "Tugboat", "Research Vessel",
    "Supply Vessel", "LNG Carrier"
  ];

  // Obtenir la liste des équipements
  const availableEquipment = Object.keys(maintenanceSchedule);

  // Calculer les heures de travail
  const calculateWorkingHours = (startTime, stopTime) => {
    const start = new Date(startTime);
    const stop = new Date(stopTime);
    return (stop - start) / (1000 * 60 * 60);
  };

  // Mettre à jour les heures de l'équipement
  const updateEquipmentHours = (equipmentName, hours) => {
    setEquipmentHours(prev => ({
      ...prev,
      [equipmentName]: (prev[equipmentName] || 0) + hours
    }));
  };

  // Vérifier la maintenance nécessaire
  const checkMaintenance = (equipmentName, totalHours) => {
    const schedule = maintenanceSchedule[equipmentName];
    if (!schedule) return [];

    const alerts = [];
    if (totalHours >= schedule.majorServiceHours) {
      alerts.push({
        type: 'major',
        message: `Major maintenance required for ${equipmentName} (${totalHours.toFixed(1)} hours)`,
        priority: 'high'
      });
    } else if (totalHours >= schedule.minorServiceHours) {
      alerts.push({
        type: 'minor',
        message: `Minor maintenance required for ${equipmentName} (${totalHours.toFixed(1)} hours)`,
        priority: 'medium'
      });
    } else if (totalHours >= schedule.inspectionHours) {
      alerts.push({
        type: 'inspection',
        message: `Inspection required for ${equipmentName} (${totalHours.toFixed(1)} hours)`,
        priority: 'low'
      });
    }
    return alerts;
  };

  // Gérer la soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();

    if (new Date(formData.workingStopTime) <= new Date(formData.workingStartTime)) {
      alert("Stop time must be after start time");
      return;
    }

    const workingHours = calculateWorkingHours(
      formData.workingStartTime,
      formData.workingStopTime
    );

    updateEquipmentHours(formData.equipmentName, workingHours);

    const newRecord = {
      id: Date.now(),
      ...formData,
      workingHours,
      dateAdded: new Date().toLocaleString()
    };

    setRecords(prev => [...prev, newRecord]);

    // Vérifier la maintenance
    const totalHours = equipmentHours[formData.equipmentName] + workingHours;
    const maintenanceNeeded = checkMaintenance(formData.equipmentName, totalHours);

    if (maintenanceNeeded.length > 0) {
      setMaintenanceAlerts(prev => [...prev, ...maintenanceNeeded]);
    }

    // Reset form
    setFormData({
      shipName: "",
      shipType: "",
      status: "navigation",
      activity: "",
      equipmentName: "",
      workingStartTime: "",
      workingStopTime: "",
      remarks: ""
    });
  };

  // Gérer les changements du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Supprimer un enregistrement
  const handleDelete = (id) => {
    const recordToDelete = records.find(record => record.id === id);
    if (recordToDelete) {
      setEquipmentHours(prev => ({
        ...prev,
        [recordToDelete.equipmentName]: Math.max(0, (prev[recordToDelete.equipmentName] || 0) - recordToDelete.workingHours)
      }));
    }
    setRecords(prev => prev.filter(record => record.id !== id));
  };

  // Charger les données du localStorage
  useEffect(() => {
    const storedRecords = JSON.parse(localStorage.getItem("records") || "[]");
    const storedHours = JSON.parse(localStorage.getItem("equipmentHours") || "{}");
    const storedAlerts = JSON.parse(localStorage.getItem("maintenanceAlerts") || "[]");

    setRecords(storedRecords);
    setEquipmentHours(storedHours);
    setMaintenanceAlerts(storedAlerts);
  }, []);

  // Sauvegarder les données dans localStorage
  useEffect(() => {
    localStorage.setItem("records", JSON.stringify(records));
    localStorage.setItem("equipmentHours", JSON.stringify(equipmentHours));
    localStorage.setItem("maintenanceAlerts", JSON.stringify(maintenanceAlerts));
  }, [records, equipmentHours, maintenanceAlerts]);

  // Rendu du formulaire
  const renderOperationsForm = () => (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="form-grid">
        <div className="form-group">
          <label htmlFor="shipName">Ship Name</label>
          <input
            type="text"
            name="shipName"
            id="shipName"
            value={formData.shipName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="shipType">Ship Type</label>
          <select
            name="shipType"
            id="shipType"
            value={formData.shipType}
            onChange={handleChange}
            required
          >
            <option value="">Select ship type</option>
            {shipTypes.map(type => (
              <option key={type} value={type.toLowerCase()}>{type}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="status">Status</label>
          <select
            name="status"
            id="status"
            value={formData.status}
            onChange={handleChange}
            required
          >
            {Object.keys(vesselStatus).map(status => (
              <option key={status} value={status}>
                {status.charAt(0).toUpperCase() + status.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="activity">Activity</label>
          <select
            name="activity"
            id="activity"
            value={formData.activity}
            onChange={handleChange}
            required
          >
            <option value="">Select activity</option>
            {activities.map(activity => (
              <option key={activity} value={activity}>{activity}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="equipmentName">Equipment</label>
          <select
            name="equipmentName"
            id="equipmentName"
            value={formData.equipmentName}
            onChange={handleChange}
            required
          >
            <option value="">Select equipment</option>
            {availableEquipment.map(equipment => (
              <option key={equipment} value={equipment}>{equipment}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="workingStartTime">Start Time</label>
          <input
            type="datetime-local"
            name="workingStartTime"
            id="workingStartTime"
            value={formData.workingStartTime}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="workingStopTime">Stop Time</label>
          <input
            type="datetime-local"
            name="workingStopTime"
            id="workingStopTime"
            value={formData.workingStopTime}
            onChange={handleChange}
            required
          />
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="remarks">Remarks</label>
        <textarea
          name="remarks"
          id="remarks"
          value={formData.remarks}
          onChange={handleChange}
          rows="3"
        />
      </div>

      <div className="flex justify-end space-x-4">
        <button
          type="button"
          onClick={() => setFormData({
            shipName: "",
            shipType: "",
            status: "navigation",
            activity: "",
            equipmentName: "",
            workingStartTime: "",
            workingStopTime: "",
            remarks: ""
          })}
          className="button secondary"
        >
          Clear
        </button>
        <button type="submit" className="button primary">
          Submit
        </button>
      </div>
    </form>
  );

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <Ship className="h-8 w-8 text-blue-600" />
              <h1 className="ml-2 text-2xl font-bold text-gray-900">
                Marine Equipment Management
              </h1>
            </div>
          </div>
        </div>

        {/* Navigation */}
        <div className="mb-6">
          <nav className="flex space-x-4">
            <button
              onClick={() => setActiveTab('dashboard')}
              className={`flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                activeTab === 'dashboard'
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              <ChartBar className="h-5 w-5 mr-2" />
              Dashboard
            </button>

            <button
              onClick={() => setActiveTab('operations')}
              className={`flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                activeTab === 'operations'
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              <Ship className="h-5 w-5 mr-2" />
              Operations
            </button>

            <button
              onClick={() => setActiveTab('financial')}
              className={`flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                activeTab === 'financial'
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              <DollarSign className="h-5 w-5 mr-2" />
              Financial
            </button>

            <button
              onClick={() => setActiveTab('maintenance')}
              className={`flex items-center px-3 py-2 text-sm font-medium rounded-md ${
                activeTab === 'maintenance'
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              <Wrench className="h-5 w-5 mr-2" />
              Maintenance
            </button>
          </nav>
        </div>

        {/* Content Section */}
        <div className="space-y-6">
          {activeTab === 'dashboard' && (
            <Dashboard
              records={records}
              equipmentHours={equipmentHours}
              maintenanceAlerts={maintenanceAlerts}
            />
          )}

          {activeTab === 'financial' && (
            <FinancialModule
              records={records}
              equipmentHours={equipmentHours}
              maintenanceAlerts={maintenanceAlerts}
            />
          )}

          {activeTab === 'operations' && (
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle>New Operation Record</CardTitle>
                </CardHeader>
                <CardContent>
                  {renderOperationsForm()}
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Operation Records</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ship</th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Activity</th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Equipment</th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Hours</th>
                          <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {records.map((record) => (
                          <tr key={record.id}>
                            <td className="px-6 py-4 whitespace-nowrap">{record.shipName}</td>
                            <td className="px-6 py-4 whitespace-nowrap">{record.shipType}</td>
                            <td className="px-6 py-4 whitespace-nowrap">{record.status}</td>
                            <td className="px-6 py-4 whitespace-nowrap">{record.activity}</td>
                            <td className="px-6 py-4 whitespace-nowrap">{record.equipmentName}</td>
                            <td className="px-6 py-4 whitespace-nowrap">{record.workingHours.toFixed(1)}</td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <button
                                onClick={() => handleDelete(record.id)}
                                className="text-red-600 hover:text-red-900"
                              >
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </CardContent>
              </Card>
            </div>
          )}

          {activeTab === 'maintenance' && (
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle>Equipment Maintenance Status</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {Object.entries(equipmentHours).map(([equipment, hours]) => (
                      <div key={equipment} className="bg-white p-4 rounded-lg shadow">
                        <h3 className="font-semibold text-lg mb-2">{equipment}</h3>
                        <p>Total Hours: {hours.toFixed(1)}</p>
                        <div className="mt-2">
                          <div className="w-full bg-gray-200 rounded-full h-2">
                            <div
                              className="bg-blue-600 h-2 rounded-full"
                              style={{
                                width: `${Math.min(
                                  (hours / maintenanceSchedule[equipment].majorServiceHours) * 100,
                                  100
                                )}%`
                              }}
                            />
                          </div>
                          <p className="text-sm text-gray-500 mt-1">
                            Next major service: {maintenanceSchedule[equipment].majorServiceHours} hours
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {maintenanceAlerts.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle>
                      <div className="flex items-center space-x-2">
                        <AlertTriangle className="h-5 w-5 text-yellow-500" />
                        <span>Maintenance Alerts</span>
                      </div>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      {maintenanceAlerts.map((alert, index) => (
                        <div
                          key={index}
                          className={`p-4 rounded-lg ${
                            alert.priority === 'high'
                              ? 'bg-red-50 text-red-700'
                              : alert.priority === 'medium'
                              ? 'bg-yellow-50 text-yellow-700'
                              : 'bg-blue-50 text-blue-700'
                          }`}
                        >
                          {alert.message}
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
